import React, { useState } from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const Navbar = ({ onSearch }) => {
  const [collapsed, setCollapsed] = useState(false); // Manage collapse state
  const [showSearch, setShowSearch] = useState(false); // Manage search bar visibility

  // Function to handle collapsing and expanding the navbar
  const handleNavCollapse = () => {
    const navbar = document.getElementById("navbarSupportedContent");
    if (navbar && navbar.classList.contains("show")) {
      navbar.classList.remove("show");
      setCollapsed(true); // Set navbar as collapsed
    } else {
      setCollapsed(false); // Set navbar as expanded
    }
  };

  // Function to toggle search bar visibility
  const handleSearchClick = () => {
    setShowSearch(!showSearch); // Toggle search bar visibility
  };

  return (
    <nav className="header_section navbar navbar-expand-lg custom_nav-container">
      <div className="container">
        {/* Brand */}
        <a className="navbar-brand" href="/">
          <span>Feane</span>
        </a>

        {/* Toggler Button */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded={collapsed ? "false" : "true"}
          aria-label="Toggle navigation"
          onClick={() => setCollapsed(!collapsed)} // Toggle collapse state
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Collapsible Content */}
        <div className={`collapse navbar-collapse ${collapsed ? "" : "show"}`} id="navbarSupportedContent">
          <ul className="navbar-nav mx-auto">
            <li className="nav-item active">
              <Link className="nav-link" to="/" onClick={handleNavCollapse}>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/menu" onClick={handleNavCollapse}>
                Menu
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/about" onClick={handleNavCollapse}>
                About
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/book" onClick={handleNavCollapse}>
                Book Table
              </Link>
            </li>
          </ul>

          {/* Search Icon */}
          <div className="user_option">
            <a href="/" className="user_link">
              <i className="fa fa-user" aria-hidden="true"></i>
            </a>
            <a className="cart_link" href="/">
              <i className="fa fa-shopping-cart" aria-hidden="true"></i>
            </a>

            {/* Search Button */}
            <button
              className="btn my-2 my-sm-0 nav_search-btn"
              type="button"
              onClick={handleSearchClick} // Toggle search bar visibility
            >
              <i className="fa fa-search" aria-hidden="true"></i>
            </button>
          </div>
        </div>
      </div>

      {/* Search Input (conditionally rendered) */}
      {showSearch && (
        <div className="search-bar">
          <input
            type="text"
            className="form-control"
            placeholder="Search Pizza or Burger"
            onChange={(event) => onSearch(event.target.value)} // Send query to parent
          />
        </div>
      )}
    </nav>
  );
};

export default Navbar;
