const Menu = ({ searchQuery }) => {
  const menuItems = [
    { id: 1, name: "Delicious Pizza", description: "Tasty cheesy pizza.", price: "$20", category: "pizza", imgSrc: "images/f1.png" },
    { id: 2, name: "Delicious Burger", description: "Juicy beef burger.", price: "$15", category: "burger", imgSrc: "images/f2.png" },
    { id: 3, name: "Delicious Pizza", description: "Pepperoni special.", price: "$17", category: "pizza", imgSrc: "images/f3.png" },
    { id: 4, name: "Delicious Pasta", description: "Creamy Alfredo.", price: "$18", category: "pasta", imgSrc: "images/f4.png" },
    { id: 5, name: "French Fries", description: "Crispy golden fries.", price: "$10", category: "fries", imgSrc: "images/f5.png" },
    { id: 6, name: "Delicious Pizza", description: "Veggie lover's choice.", price: "$15", category: "pizza", imgSrc: "images/f6.png" },
    { id: 7, name: "Tasty Burger", description: "Chicken burger.", price: "$12", category: "burger", imgSrc: "images/f7.png" },
    { id: 8, name: "Tasty Burger", description: "Spicy grilled burger.", price: "$14", category: "burger", imgSrc: "images/f8.png" },
    { id: 9, name: "Delicious Pasta", description: "Spaghetti Bolognese.", price: "$10", category: "pasta", imgSrc: "images/f9.png" },
  ];

  // Ensure searchQuery is not undefined
  const query = searchQuery ? searchQuery.toLowerCase() : "";

  // Filter menu items safely
  const filteredItems = menuItems.filter((item) => {
    const name = item.name ? item.name.toLowerCase() : "";
    const category = item.category ? item.category.toLowerCase() : "";
    return name.includes(query) || category.includes(query);
  });

  return (
    <div className="filters-content">
      <div className="row grid">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <div key={item.id} className={`col-sm-6 col-lg-4 all ${item.category}`}>
              <div className="box">
                <div className="img-box">
                  <img src={item.imgSrc} alt={item.name} />
                </div>
                <div className="detail-box">
                  <h5>{item.name}</h5>
                  <p>{item.description}</p>
                  <div className="options">
                    <h6>{item.price}</h6>
                  </div>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No items match your search.</p>
        )}
      </div>
    </div>
  );
};

export default Menu;
