import React, { useState } from "react";

const Menu = () => {
  const menuItems = [
    { id: 1, name: "Delicious Pizza", description: "Tasty pizza description.", price: "$20", category: "pizza", imgSrc: "images/f1.png" },
    { id: 2, name: "Delicious Burger", description: "Tasty burger description.", price: "$15", category: "burger", imgSrc: "images/f2.png" },
    { id: 3, name: "Tasty Pasta", description: "Delicious pasta description.", price: "$18", category: "pasta", imgSrc: "images/f3.png" },
    { id: 4, name: "French Fries", description: "Crispy fries description.", price: "$10", category: "fries", imgSrc: "images/f4.png" },
  ];

  const [filter, setFilter] = useState("all");

  // Filter menu items based on the selected filter
  const filteredItems = filter === "all" ? menuItems : menuItems.filter((item) => item.category === filter);

  return (
    <div className="menu-section">
      <h2>Our Menu</h2>
      <ul className="filters-menu">
        {["all", "burger", "pizza", "pasta", "fries"].map((category) => (
          <li
            key={category}
            className={filter === category ? "active" : ""}
            onClick={() => setFilter(category)}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </li>
        ))}
      </ul>
      <div className="menu-items">
        {filteredItems.map((item) => (
          <div key={item.id} className="menu-item">
            <img src={item.imgSrc} alt={item.name} />
            <h5>{item.name}</h5>
            <p>{item.description}</p>
            <h6>{item.price}</h6>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
