import React from 'react';
import Menu from './Menu';
import Book from './Book';
import About from './About';

// Carousel Component
const Carousel = () => {
  const slides = [
    {
      id: 1,
      title: "Fast Food Restaurant",
      description: "Doloremque, itaque aperiam facilis rerum, commodi, temporibus sapiente ad mollitia laborum quam quisquam esse error unde.",
      buttonText: "Order Now",
    },
    {
      id: 2,
      title: "Fast Food Restaurant",
      description: "Doloremque, itaque aperiam facilis rerum, commodi, temporibus sapiente ad mollitia laborum quam quisquam esse error unde.",
      buttonText: "Order Now",
    },
    {
      id: 3,
      title: "Fast Food Restaurant",
      description: "Doloremque, itaque aperiam facilis rerum, commodi, temporibus sapiente ad mollitia laborum quam quisquam esse error unde.",
      buttonText: "Order Now",
    },
  ];

  return (
    <section className="slider_section">
      <div id="customCarousel1" className="carousel slide" data-ride="carousel">
        <div className="carousel-inner">
          {slides.map((slide, index) => (
            <div key={slide.id} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
              <div className="container">
                <div className="row">
                  <div className="col-md-7 col-lg-6">
                    <div className="detail-box">
                      <h1>{slide.title}</h1>
                      <p>{slide.description}</p>
                      <div className="btn-box">
                        <a href="#" className="btn1">{slide.buttonText}</a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="container">
          <ol className="carousel-indicators">
            {slides.map((_, index) => (
              <li key={index} data-target="#customCarousel1" data-slide-to={index} className={index === 0 ? 'active' : ''}></li>
            ))}
          </ol>
        </div>
      </div>
    </section>
  );
};

// Offer Section Component
const OfferSection = () => {
  const offers = [
    { id: 1, title: "Tasty Thursdays", discount: "20%", imgSrc: "images/o1.jpg", buttonText: "Order Now" },
    { id: 2, title: "Pizza Days", discount: "15%", imgSrc: "images/o2.jpg", buttonText: "Order Now" },
  ];

  return (
    <section className="offer_section layout_padding-bottom">
      <div className="offer_container">
        <div className="container">
          <div className="row">
            {offers.map(offer => (
              <div key={offer.id} className="col-md-6">
                <div className="box">
                  <div className="img-box">
                    <img src={offer.imgSrc} alt={offer.title} />
                  </div>
                  <div className="detail-box">
                    <h5>{offer.title}</h5>
                    <h6><span>{offer.discount}</span> Off</h6>
                    <a href="#">{offer.buttonText}</a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

// Client Section Component
const ClientSection = () => {
  const clients = [
    { name: "Moana Michell", review: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.", imgSrc: "images/client1.jpg" },
    { name: "Mike Hamell", review: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.", imgSrc: "images/client2.jpg" },
  ];

  return (
    <section className="client_section layout_padding-bottom">
      <div className="container">
        <div className="heading_container heading_center psudo_white_primary mb_45">
          <h2>What Says Our Customers</h2>
        </div>
        <div className="carousel-wrap row">
          <div className="owl-carousel client_owl-carousel">
            {clients.map((client, index) => (
              <div key={index} className="item">
                <div className="box">
                  <div className="detail-box">
                    <p>{client.review}</p>
                    <h6>{client.name}</h6>
                    <p>magna aliqua</p>
                  </div>
                  <div className="img-box">
                    <img src={client.imgSrc} alt={client.name} className="box-img" />
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

// Home Component
const Home = () => {
  return (
    <div>
      <div className="hero_area">
        <div className="bg-box">
          <img src="images/hero-bg.jpg" alt="Hero Background" />
        </div>
      </div>

      <Carousel />
      <OfferSection />
      <Menu />
      <About />
      <Book />
      <ClientSection />
    </div>
  );
};

export default Home;
